n          filterMap.put(\"/user/*\",\"authc\");\n  \n          bean.setFilterChainDefinitionMap(filterMap);\n  \n          //ËÆæÁΩÆÁôªÂΩïÁöÑËØ∑Ê±Ç\n          bean.setLoginUrl(\"/toLogin\");\n  \n          //‰∏∫ÊéàÊùÉÈ°µÈù¢\n          bean.setUnauthorizedUrl(\"/noauto\");\n  \n        return bean;\n      }\n  \n      //DefaultWebSecurityManager\n  \n      @Bean\n      public DefaultWebSecurityManager getDefaultWebSecurityManager(@Qualifier(\"userRealm\") UserRealm userRealm) {\n          DefaultWebSecurityManager securityManager = new DefaultWebSecurityManager();\n  \n          // ÂÖ≥ËÅîuserRealm\n          securityManager.setRealm(userRealm);\n          return securityManager;\n      }\n      //ÂàõÂª∫realmÂØπË±°ÔºåÈúÄË¶ÅËá™ÂÆö‰πâÁ±ª\n  \n      @Bean\n      public UserRealm userRealm() {\n          return new UserRealm();\n      }\n  \n      // Êï¥ÂêàShiroDialectÔºö Áî®Êù•Êï¥Âêà Shiro thymeleaf\n      @Bean\n      public ShiroDialect getShiroDialect() {\n          return new ShiroDialect();\n      }\n  }\n  ```\n\n- UserRealm\n\n  ```java\n  package nuc.ss.config;\n  \n  import nuc.ss.pojo.User;\n  import nuc.ss.service.UserService;\n  import org.apache.shiro.SecurityUtils;\n  import org.apache.shiro.authc.*;\n  import org.apache.shiro.authz.AuthorizationInfo;\n  import org.apache.shiro.authz.SimpleAuthorizationInfo;\n  import org.apache.shiro.realm.AuthorizingRealm;\n  import org.apache.shiro.session.Session;\n  import org.apache.shiro.subject.PrincipalCollection;\n  import org.apache.shiro.subject.Subject;\n  import org.springframework.beans.factory.annotation.Autowired;\n  \n  //Ëá™ÂÆö‰πâÁöÑUserRealm\n  public class UserRealm extends AuthorizingRealm {\n  \n      @Autowired\n      UserService userService;\n      //ÊéàÊùÉ\n      @Override\n      protected AuthorizationInfo doGetAuthorizationInfo(PrincipalCollection principalCollection) {\n          System.out.println(\"ÊâßË°å‰∫Ü=>ÊéàÊùÉdoGetAuthorizationInfo\");\n  \n          SimpleAuthorizationInfo info = new SimpleAuthorizationInfo();\n  \n          //info.addStringPermission(\"user:add\");\n  \n          //ÊãøÂà∞ÂΩìÂâçÁôªÂΩïÁöÑËøô‰∏™ÂØπË±°\n          Subject subject = SecurityUtils.getSubject();\n          User currentUser = (User)subject.getPrincipal();//ÊãøÂà∞userÂØπË±°\n  \n          //ËÆæÁΩÆÂΩìÂâçÁî®Êà∑ÁöÑÊùÉÈôê\n          info.addStringPermission(currentUser.getPerms());\n  \n          return info;\n      }\n  \n      //ËÆ§ËØÅ\n      @Override\n      protected AuthenticationInfo doGetAuthenticationInfo(AuthenticationToken token) throws AuthenticationException {\n          System.out.println(\"ÊâßË°å‰∫Ü=>ËÆ§ËØÅdoGetAuthorizationInfo\");\n  \n          UsernamePasswordToken userToken = (UsernamePasswordToken) token;\n  \n          // ËôöÊãüÁî®Êà∑\n          //String name = \"root\";\n          //String password = \"123456\";\n          //if (!userToken.getUsername().equals(name)) {\n          //    return null;//ÊäõÂá∫ÂºÇÂ∏∏ UnknownAccountException\n          //}\n  \n          // ÁúüÂÆûÊï∞ÊçÆÂ∫ì Áî®Êà∑Âêç„ÄÅÂØÜÁ†ÅÔºå Êï∞ÊçÆ‰∏≠Âèñ\n          User user = userService.queryUserByName(userToken.getUsername());\n  \n          if (user == null) {//Ê≤°ÊúâËøô‰∏™‰∫∫\n              return null;\n          }\n  \n          //È¶ñÈ°µ\n          //Subject currentSubject = SecurityUtils.getSubject();\n          //Session session = currentSubject.getSession();\n          //session.setAttribute(\"loginUser\",user);\n  \n  \n          // ÂØÜÁ†ÅËÆ§ËØÅÔºåshiroÂÅö\n          return new SimpleAuthenticationInfo(user,user.getPwd(),\"\");\n      }\n  }\n  ```\n\n- MyController\n\n  ```java\n  package nuc.ss.controller;\n  \n  import org.apache.shiro.SecurityUtils;\n  import org.apache.shiro.authc.IncorrectCredentialsException;\n  import org.apache.shiro.authc.UnknownAccountException;\n  import org.apache.shiro.authc.UsernamePasswordToken;\n  import org.apache.shiro.subject.Subject;\n  import org.springframework.stereotype.Controller;\n  import org.springframework.ui.Model;\n  import org.springframework.web.bind.annotation.RequestMapping;\n  import org.springframework.web.bind.annotation.ResponseBody;\n  \n  @Controller\n  public class MyController {\n  \n      @RequestMapping({\"/\",\"/index\"})\n      public String toIndex(Model model) {\n          model.addAttribute(\"msg\",\"hello,Shiro\");\n          return \"index\";\n      }\n  \n      @RequestMapping(\"/user/add\")\n      public String add() {\n          return \"user/add\";\n      }\n  \n      @RequestMapping(\"/user/update\")\n      public String update() {\n          return \"user/update\";\n      }\n  \n      @RequestMapping(\"/toLogin\")\n      public String toLogin() {\n          return \"login\";\n      }\n  \n      @RequestMapping(\"/login\")\n      public String login(String username, String password, Model model) {\n          //Ëé∑Âèñ‰∏Ä‰∏™Áî®Êà∑\n          Subject subject = SecurityUtils.getSubject();\n          // Â∞ÅË£ÖÁî®Êà∑ÁöÑÁôªÂΩïÊï∞ÊçÆ\n          UsernamePasswordToken token = new UsernamePasswordToken(username, password);\n  \n          try {\n              subject.login(token);//ÊâßË°åÁôªÂΩïÁöÑÊñπÊ≥ïÔºåÂ¶ÇÊûúÊ≤°ÊúâÂºÇÂ∏∏Â∞±ËØ¥Êòéok‰∫Ü\n              return \"index\";\n          } catch (UnknownAccountException e) {//Áî®Êà∑Âêç‰∏çÂ≠òÂú®\n              model.addAttribute(\"msg\",\"Áî®Êà∑ÂêçÈîôËØØ\");\n              return \"login\";\n          } catch (IncorrectCredentialsException e) {//ÂØÜÁ†Å‰∏çÂ≠òÂú®\n              model.addAttribute(\"msg\",\"ÂØÜÁ†ÅÈîôËØØ\");\n              return \"login\";\n          }\n      }\n  \n      @RequestMapping(\"/noauto\")\n      @ResponseBody\n      public String unauthorized() {\n          return \"Êú™ÁªèÊéàÊùÉÔºåÊó†Ê≥ïËÆøÈóÆÊ≠§È°µÈù¢\";\n      }\n  }\n  \n  ```\n\n- pom‰æùËµñ\n\n  ```xml\n  <?xml version=\"1.0\" encoding=\"UTF-8\"?>\n  <project xmlns=\"http://maven.apache.org/POM/4.0.0\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\"\n           xsi:schemaLocation=\"http://maven.apache.org/POM/4.0.0 https://maven.apache.org/xsd/maven-